import "./styles.css";

// settings
const AMOUNT = 10; // 1 ... 10
const FLAKES_SIZE_DIFFERENCE = 10; // 1 - more similar; 10 - more difference
const MAX_FLAKE_SIZE = 16; // 2...16 in px
const FLAKE_IMAGE_URL =
  "data:image/png;base64,";
// settings

const amountMultiplier = AMOUNT / 2;

const FLAKES_PER_SECOND = 15 * amountMultiplier;
const MAX_FLAKES_COUNT = 200 * amountMultiplier;

// checkers
if (AMOUNT > 10 || AMOUNT < 1)
  throw new Error("Speed should be between 1 and 10");

if (MAX_FLAKE_SIZE > 16 || MAX_FLAKE_SIZE < 2) {
  throw new Error("Size should be between 2 and 16");
}

if (FLAKES_SIZE_DIFFERENCE > 10 || FLAKES_SIZE_DIFFERENCE < 1) {
  throw new Error("Flakes size difference should be between 1 and 10");
}
// checkers

const FLAKES = {};

const tooMuchFlakes = () => Object.keys(FLAKES).length > MAX_FLAKES_COUNT;

const flakeImg = new Image();
flakeImg.src = FLAKE_IMAGE_URL;

class Flake {
  constructor(ctx, id) {
    this.ctx = ctx;
    this.x = Math.random() * this.ctx.canvas.width;
    this.y = 0;
    this.id = id;
    this.img = flakeImg;
    this.xStep = Math.random() * 2 - 1;
    this.yStep = 1 + Math.random();
    this.radius = MAX_FLAKE_SIZE - Math.random() * FLAKES_SIZE_DIFFERENCE;
  }

  draw() {
    this.ctx.drawImage(this.img, this.x, this.y, this.radius, this.radius);

    if (this.x >= this.ctx.canvas.width || this.y >= this.ctx.canvas.height) {
      delete FLAKES[this.id];
      this.img.remove();
    } else {
      this.x = this.x + this.xStep;
      this.y = this.y + this.yStep;
    }
  }
}

const generateFlake = (ctx, i) => {
  const id = Date.now() + "" + i;
  setTimeout(() => {
    FLAKES[id] = new Flake(ctx, id);
  }, 200 + Math.random() * 800);
};

const drawFlakes = ctx => {
  ctx.globalCompositeOperation = "destination-over";
  ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
  Object.keys(FLAKES).forEach(key => {
    const flake = FLAKES[key];
    flake.draw();
  });

  window.requestAnimationFrame(drawFlakes.bind(this, ctx));
};

const canvasElement = document.getElementById("app");

if (canvasElement) {
  const ctx = canvasElement.getContext("2d");
  canvasElement.width = window.innerWidth;
  canvasElement.height = window.innerHeight;

  setInterval(() => {
    for (let i = 0; i < FLAKES_PER_SECOND; i += 1) {
      if (!tooMuchFlakes()) generateFlake(ctx, i);
    }
  }, 1000);

  window.requestAnimationFrame(drawFlakes.bind(this, ctx));
}
